%spark
//Create RDDs from Scala collections
val lsRdd = sc.parallelize(List(1,2,3,4,5))

//number of items in lsRdd
val count = lsRdd.count

//first element in lsRdd
val firstE = lsRdd.first

//Number of partitions
val partitionsNum = lsRdd.partitions.length

//Manipulating lsRDD
val dupRdd = lsRdd.flatMap(i => List.fill(i)(i))
val dupArray = dupRdd.collect
val evens = dupRdd.filter(_%2 == 0).collect

