%spark
//Check CSV format
val splitRdd = retailRDD.map(s => s.split(","))

//Some lines have more than 8 column which indicates a format issue
val samples = splitRdd.map(arr => arr.length).takeSample(false,15, 11)

//find out how many lines have more than 8 cols
val lenArrRdd = splitRdd.map(arr => (arr.length, arr))
lenArrRdd.filter(_._1 != 8).take(3).foreach({case(count, cols) => println(count + ":" + cols.mkString("||"))})

